import { Account, UnsignedTxPayload, SignResult, Provider } from '@unique-nft/accounts/types';

declare class MetamaskAccount extends Account<string> {
    private signer;
    constructor(address: string);
    formatAddress(prefix?: number | 'ethereum'): string;
    sign(unsignedTxPayload: UnsignedTxPayload): Promise<SignResult>;
}

declare class MetamaskProvider extends Provider<string> {
    init(): Promise<void>;
    getAccounts(): Promise<Account<string>[]>;
    forgetAccount(account: Account<string>): void;
}

export { MetamaskAccount, MetamaskProvider };
